    var count = 0;
    var robotBlab = "BLABL"

    var robotLang = () => {
      if(count > 4) {
        count = 0;
      }
      blah += robotBlab[count];
      count++

    } 

    for (var i = 0; i < this.props.userInput.length; i++){
          robotLang()
    }

    return(
      <div>
          <h2>I hear you say {blah}? Is that Correct?</h2>
      </div>
    )
  }
}

export default BadRobot;
